import React,{useState} from "react";
import './ExpenseForm.css'


function ExpenseForm(){
    const [eneteredTitle, seteneteredTitle] = useState('')
    const [eneteredAmount, seteneteredAmount] = useState('')
    const [eneteredDate, seteneteredDate] = useState('')

    const titleHander = (event)=>{
        seteneteredTitle(event.target.value)
    }
    const amountHander = (event)=>{
        seteneteredAmount(event.target.value)
    }
    const dateHander = (event)=>{
        seteneteredDate(event.target.value)
    }
    return(
        <form action="">
            <div className='new-expense__controls'>
                <div className='new-expense__control'>
                    <label>Title</label>
                    <input type='text' onChange={titleHander}/>
                </div>
                <div className='new-expense__control'>
                    <label>Amount</label>
                    <input type='number' min='0.01' step='0.01' onChange={amountHander}/>
                </div>
                <div className='new-expense__control'>
                    <label>Date</label>
                    <input type='date' min='2020-01-01' max='2023-01-12' onChange={dateHander}/>
                </div>

            </div>
            <div className='new-expense__actions'>
                <button type='submit'>Submit</button>
            </div>
        </form>
    )
}

export default ExpenseForm;
