import React, {useState} from "react";
import ExpenseItem from "./ExpenseItem";
import './Expenses.css'
import Card from "../UI/Card";
import ExpensesFilter from "../NewExpenses/ExpensesFilter";

function Expenses(props){
    const [filteredYear, setFilteredYear] = useState('2018')
      const year = props.items.filter(item => item.date.getFullYear() == filteredYear)
     console.log(year)
    const saveSeleceteddateHandler = (selectedDate) =>{
        setFilteredYear(selectedDate);
    }
    return(
        <Card className='expenses'>
             <ExpensesFilter seleceted={filteredYear} onSaveSelectedDate={saveSeleceteddateHandler}/>
            {props.items.map(expense => <ExpenseItem key={expense.id} title={expense.title} amount={expense.amount} date={expense.date}/>)}

        </Card>
    )
}

export  default Expenses;
